import {Upload, message, Button, Space, Image, Col, Row} from 'antd';
import {LoadingOutlined, PlusOutlined, UploadOutlined} from '@ant-design/icons';
import React from "react";
import {getHeaderFromLocalStorage} from "../../utils/fetch-request";
import styled from "styled-components";

function getBase64(img, callback) {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsDataURL(img);
}

const UploadContainer = styled('div')`
    display:flex;
    flex-direction:column;
`

class CopyMoveUploadImage extends React.Component {
    state = {
        imageUrl: '',
        name: '',
    };

    handleCustomRequest = () => {
        this.props.singleFaceImageDetection({
            uploadData: this.state.imageUrl,
            userId: getHeaderFromLocalStorage('id'),
        })
    }
    handleBeforeUpload = info => {
        getBase64(info, imageUrl => {
            this.setState({
                imageUrl: imageUrl,
                name: info.name
            })
        })
        return false;
    }

    render() {
        const {imageUrl, name} = this.state;
        return (
            <Row gutter={[16,16]}>
                <Col span={4}>
                    <Upload
                        showUploadList={false}
                        maxCount={1}
                        onChange={this.handleChange}
                        beforeUpload={this.handleBeforeUpload}
                    >
                        <Button icon={<UploadOutlined/>}>选择图片</Button>
                    </Upload>
                </Col>
                <Col span={12}>
                    <div style={{display: 'flex',justifyContent:'space-between'}}>
                        <div>{name}</div>
                        <Button onClick={this.handleCustomRequest}>单张检测</Button>
                    </div>
                </Col>
                <Col span={20} push={4}>
                    {imageUrl ? <img src={imageUrl} alt="avatar" style={{width: '400px', height: '400px'}}/> :
                        <Image
                            width={400}
                            height={400}
                            src="error"
                            fallback="data:image/png;base64,"
                        />
                    }
                </Col>
            </Row>
        );
    }
}

export default CopyMoveUploadImage
