import React, {useEffect, useState} from 'react';
import {Typography, Image, Col} from 'antd';
import {Table} from 'antd';
import {getHeaderFromLocalStorage} from "../../utils/fetch-request";
import SecretImage from '../../images/videoSecret.png'

const {Title} = Typography;

const columns = [
    {
        title: '序号',
        dataIndex: 'id',
        sorter: true,
        width: '10%',
    },
    {
        title: '分解1',
        dataIndex: 'pictureOnePosition',
        width: '20%',
        render: (text) => {
            console.log(text)
            return text !== null ? <Image src={text} width='80px' height='80px' preview='false'
                                          fallback={'data:image/png;base64,'}/>
                : <Image
                    width='80px' height='80px' preview='false'
                    src={SecretImage}
                />
        }
    },
    {
        title: '分解2',
        dataIndex: 'pictureTwoPosition',
        width: '20%',
        render: (text) => {
            return text !== null ? <Image src={text} width='80px' height='80px' preview='false'
                                          fallback={'data:image/png;base64,'}/>
                : <Image
                    width='80px' height='80px' preview='false'
                    src={SecretImage}
                />
        }
    },
    {
        title: '开始时间',
        dataIndex: 'startTime',
        width: '15%',
    },
    {
        title: '结束时间',
        dataIndex: 'endTime',
        width: '15%',
    },
    {
        title: '检测结果',
        dataIndex: 'result',
        render: (text) => {
            if (text === null) {
                text = "[];[]"
            }
            text = text.split(";")
            return <div>
                结果1：{text[0]}<br/>
                结果2：{text[1]}
            </div>
        }
    },
];

const FaceHistory = ({getFaceHistory, faceHistory}) => {
    const [state, setState] = useState({
        page: 1,
    })
    const {currentPage} = state;

    const changeState = (data) => {
        setState({...state, ...data})
    }

    useEffect(() => {
        getFaceHistory(currentPage, getHeaderFromLocalStorage('user'));
    }, [])

    const changePage = currentPage => {
        changeState({currentPage})
        getFaceHistory(currentPage, getHeaderFromLocalStorage('user'));
    }

    return (
        <Typography>
            <Title>肖像取证历史记录</Title>
            <Table columns={columns}
                   dataSource={faceHistory.content}
                   pagination={{
                       onChange: changePage,
                       current: currentPage,
                       showSizeChanger: false,
                       total: faceHistory.totalElements
                   }}
            />
        </Typography>
    )
}


export default FaceHistory
