import React, {useState} from 'react';
import {Button, Row, Steps, Col, Popover, Image, Input, Progress} from 'antd';
import {saveAs} from 'file-saver'
import {
    SearchOutlined,
    DownloadOutlined,
    SolutionOutlined,
    SmileOutlined
} from '@ant-design/icons';
import EdgeServer from '../../images/edgeServerOne1.png';
import Reaction1 from '../../images/1.png';
import Reaction2 from '../../images/2.png';


const {Step} = Steps;

const {TextArea} = Input;

const Detection = ({pictureOnePosition, pictureTwoPosition, detectionId, beginDetection, getDetectionDetail, result, textAreaValue, current, status}) => {

    const [state, setState] = useState({
        flag: false
    })
    const {flag} = state

    if (detectionId === 0 || flag === false) {
        textAreaValue = []
        result = []
        current = 0
    }

    const begin = () => {
        beginDetection(detectionId);
        getDetectionDetail(detectionId);
        setState({
            flag: true
        })
    }

    const saveFile = () => {
        let str = new Blob([textAreaValue], {type: 'text/plain;charset=utf-8'})
        saveAs(str, '交互详情.txt')
    }

    const listItems = textAreaValue.map((item, index) =>
        <li key={index}>
            {item}
        </li>
    );
    return (
        <div>
            {detectionId === 0 ? <Button icon={<SearchOutlined/>} onClick={begin} disabled>开始检测</Button> :
                <Button icon={<SearchOutlined/>} onClick={begin}>开始检测</Button>}
            {current === 2 ? <Button icon={<DownloadOutlined/>} style={{marginLeft: "20px"}} onClick={saveFile}>
                下载交互详情
            </Button> : <Button icon={<DownloadOutlined/>} disabled style={{marginLeft: "20px"}}>下载交互详情</Button>}
            <Row align="middle" style={{marginTop: "20px"}} justify="space-around" wrap="false" gutter={[0, 5]}>
                <Col span={23}>
                    <Steps current={current}>
                        <Step title="随机数生成" description="离线阶段生成所需的所有随机数"/>
                        <Step title="检测中" description="双方边缘服务器交互"/>
                        <Step title="完成检测" description="检测完成"/>
                    </Steps>
                </Col>
                <Col offset={1}>
                    <Image
                        height={130}
                        src={pictureOnePosition}
                        preview={false}
                        fallback="data:image/png;base64,"
                    />
                </Col>
                <Col>
                    <Image src={EdgeServer} preview={false} height={170}/>
                </Col>
                <Col span={3}>
                    结果：{result[0]}
                </Col>
                <Col offset={6} span={2}>
                    <Image src={Reaction1} preview={false} height={170}/>
                </Col>
                <Col span={6}>
                    <div style={{maxHeight: '150px', overflow: 'auto', backgroundColor: 'black', color: 'white'}}>
                        {listItems}
                    </div>
                </Col>
                <Col span={10}>
                    <Image src={Reaction2} preview={false} height={170}/>
                </Col>
                <Col offset={1}>
                    <Image
                        height={130}
                        src={pictureTwoPosition}
                        preview={false}
                        fallback="data:image/png;base64,"
                    />
                </Col>
                <Col>
                    <Image src={EdgeServer} preview={false} height={170}/>
                </Col>
                <Col span={3}>
                    结果：{result[1]}
                </Col>
                {/*<Col span={2} style={{marginTop: '20px'}}>*/}
                {/*    检测进度：*/}
                {/*</Col>*/}
                {/*<Col span={22} style={{marginTop: '20px'}}>*/}
                {/*    <Progress percent={textAreaValue.length} status="active"/>*/}
                {/*</Col>*/}
            </Row>
            {/*<Row style={{marginTop: "20px"}} gutter={[24, 20]}>*/}
            {/*    <Col span={24}>交互详情：*/}

            {/*    </Col>*/}
            {/*    /!*<Col span={24}>*!/*/}
            {/*    /!*    /!*<TextArea disabled maxLength={100} style={{height: 120, overflow: "visible",backgroundColor:'black',color:'white'}}*!/*!/*/}
            {/*    /!*    /!*          value={textAreaValue}/>*!/*!/*/}
            {/*    /!*    <ul>{listItems}</ul>*!/*/}
            {/*    /!*</Col>*!/*/}
            {/*</Row>*/}

        </div>
    )
}

export default Detection
