import {Upload, Button, Image, Col, Row, Tooltip} from 'antd';
import {LoadingOutlined, UploadOutlined} from '@ant-design/icons';
import React, {useState} from "react";
import '../../css/index.css'
import Tips from "../../images/tips.png";
import SecretImage from '../../images/videoSecret.png'
import EqualIcon from "../../images/equalIcon.png";
import AddIcon from "../../images/addIcon.png";
import styled from "styled-components";
import * as utils from "../../utils/fetch-request";

const action = '/api/face/encryptedVideo/' + utils.getHeaderFromLocalStorage('user')

const Tip = styled('img')`
     width:70px;
`

function getBase64(img, callback) {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsDataURL(img);
}


const VideoUpload = () => {
    const [state, setState] = useState({
        name: '',
        loading: false,
        videoPosition: '',
        secretImage: 'data:image/png;base64,'
    })

    const handleCustomRequest = info => {
        if (info.file.status === 'uploading') {
            setState({
                loading: true
            });
            return;
        }
        if (info.file.status === 'done') {
            setState({
                videoPosition: info.file.response.position,
                loading: false,
                secretImage: SecretImage
            })
        }
    }
    const text = (<span>我们会将您上传的视频进行加性秘密分解</span>);
    const {loading, videoPosition, secretImage} = state;
    const uploadButton = (
        <div>
            <div>{loading ? <LoadingOutlined/> : ""}</div>
            <Image
                height={100}
                src="error"
                preview={false}
                fallback="data:image/png;base64,"
            />
        </div>
    );
    return (
        <div>
            <Row justify="space-around " align="middle">
                <Col span={4}>
                    <Upload
                        name="file"
                        showUploadList={false}
                        maxCount={1}
                        action={action}
                        onChange={handleCustomRequest}
                    >
                        <Button icon={<UploadOutlined/>}>上传视频</Button>
                    </Upload>
                </Col>
                <Col span={20}>
                    <Tooltip placement="right" title={text}>
                        <Tip src={Tips}/>
                    </Tooltip>
                </Col>
                <Col>
                    {videoPosition !== '' ?
                        <video width="120" height="100" controls src={videoPosition}/>
                        : uploadButton}
                </Col>
                <Col>
                    <img src={EqualIcon}/>
                </Col>
                <Col>
                    <Image
                        height={100}
                        preview={false}
                        src={secretImage}
                    />
                </Col>
                <Col>
                    <img src={AddIcon}/>
                </Col>
                <Col>
                    <Image
                        height={100}
                        preview={false}
                        src={secretImage}
                    />
                </Col>
            </Row>
        </div>
    );
}

export default VideoUpload
